void Screen1ViewBase::setupScreen()
{
    ScrollList.initialize();
    for (int i = 0; i < ScrollListListItems.getNumberOfDrawables(); i++)
    {
        ScrollListListItems[i].initialize();
    }
}

//Handles when a key is pressed
void Screen1ViewBase::handleKeyEvent(uint8_t key)
{
    if(0 == key)
    {
        //FlowSimInteraction
        //When hardware button 0 clicked call virtual function
        //Call FlowSim
        FlowSim();
    }
}

void Screen1ViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &ExitButton)
    {
        //ExitSettingsInteraction
        //When ExitButton clicked call virtual function
        //Call ExitSettings
        ExitSettings();
    }
    else if (&src == &PourButton)
    {
        //PourInteraction
        //When PourButton clicked call virtual function
        //Call PourDrink
        PourDrink();
    }
    else if (&src == &SettingsButton)
    {
        //SettingsInteraction
        //When SettingsButton clicked call virtual function
        //Call SettingsScreen
        SettingsScreen();
    }
    else if (&src == &Button250mL)
    {
        //Button250mLInteraction
        //When Button250mL clicked call virtual function
        //Call Click250mL
        Click250mL();
    }
    else if (&src == &Button500mL)
    {
        //Button500mLInteraction
        //When Button500mL clicked call virtual function
        //Call Click500mL
        Click500mL();
    }
    else if (&src == &Button750mL)
    {
        //Button750mLInteraction
        //When Button750mL clicked call virtual function
        //Call Click750mL
        Click750mL();
    }
    else if (&src == &Pump1Change)
    {
        //Pump1ChangeInteraction
        //When Pump1Change clicked call virtual function
        //Call ChangePump1
        ChangePump1();
    }
    else if (&src == &Pump2Change)
    {
        //Pump2ChangeInteraction
        //When Pump2Change clicked call virtual function
        //Call ChangePump2
        ChangePump2();
    }
    else if (&src == &Pump3Change)
    {
        //Pump3ChangeInteraction
        //When Pump3Change clicked call virtual function
        //Call ChangePump3
        ChangePump3();
    }
    else if (&src == &Pump4Change)
    {
        //Pump4ChangeInteraction
        //When Pump4Change clicked call virtual function
        //Call ChangePump4
        ChangePump4();
    }
    else if (&src == &SelectTodo1_1)
    {
        //Todo2Interaction
        //When SelectTodo1_1 clicked call virtual function
        //Call ToTodo2
        ToTodo2();
    }
    else if (&src == &SelectTodo1)
    {
        //Todo1Interaction
        //When SelectTodo1 clicked call virtual function
        //Call ToTodo1
        ToTodo1();
    }
    else if (&src == &SelectVodka)
    {
        //VodkaInteraction
        //When SelectVodka clicked call virtual function
        //Call ToVodka
        ToVodka();
    }
    else if (&src == &SelectTequila)
    {
        //TequilaInteraction
        //When SelectTequila clicked call virtual function
        //Call ToTequila
        ToTequila();
    }
    else if (&src == &SelectRum)
    {
        //RumInteraction
        //When SelectRum clicked call virtual function
        //Call ToRum
        ToRum();
    }
    else if (&src == &SelectGin)
    {
        //GinInteraction
        //When SelectGin clicked call virtual function
        //Call ToGin
        ToGin();
    }
    else if (&src == &SelectNone)
    {
        //NoneInteraction
        //When SelectNone clicked call virtual function
        //Call ToNone
        ToNone();
    }
    else if (&src == &SelectWater)
    {
        //WaterInteraction
        //When SelectWater clicked call virtual function
        //Call ToWater
        ToWater();
    }
    else if (&src == &SelectJuice)
    {
        //JuiceInteraction
        //When SelectJuice clicked call virtual function
        //Call ToJuice
        ToJuice();
    }
}

void Screen1ViewBase::updateItemCallbackHandler(touchgfx::DrawableListItemsInterface* items, int16_t containerIndex, int16_t itemIndex)
{
    if (items == &ScrollListListItems)
    {
        touchgfx::Drawable* d = items->getDrawable(containerIndex);
        imageContainer* cc = (imageContainer*)d;
        ScrollListUpdateItem(*cc, itemIndex);
    }
}
